import React from 'react';
import { Button, CardBody, CardImg, CardTitle } from 'react-bootstrap';
import { Card } from 'react-bootstrap';

function Content(props) {

    function handleclick(id) {
        props.onDelete(props.id);
    }

    const buttonstyle={
        position: "absolute",
        right: "18px",
        bottom: "-18px",
        background: "#f5ba13",
        color: "#fff",
        border: "none",
        borderRadius: "50%",
        width: "36px",
        height: "36px",
        boxShadow: "0 1px 3px rgba(0, 0, 0, 0.3)",
        cursor: pointer,
        outline: none,
    }

  return (
    <div style={{display: "inline-block"}}>
        <Card style={{width: "18rem"}}>
            <CardImg variant='top' src={props.path} />
            <CardBody>
                <CardTitle>{props.id}</CardTitle>
                <Button variant="primary" onClick={handleclick} style={buttonstyle}>Delete</Button>
            </CardBody>
        </Card>
    </div>
  )
}

export default Content;
