import React, { useState } from "react";
import './Forms.css';

function Post(props) {

    const [inputpost, setinputpost]=useState({
        src:"",
        alt:"",
    })

    function handlechange(event){
        const {name, value}=event.target;
        setinputpost(prevpost=>{
            return {
                ...prevpost,
                [name]:value,
            }
        })
    }

    function handleclick(event) {
        props.onAdd(inputpost);
        setinputpost({
            src:"",
            alt:"",
        })
        event.preventDefault();
    }

    return <div>
        <form>
            <input type="text" name="alt" value={inputpost.alt} placeholder="Username" onChange={handlechange} required /><br />
            <input type="password" placeholder="Password" onChange={handlechange} required /><br />
            <input type="file" name="src" value={inputpost.src} required /><br />
            <button onClick={handleclick}>Post</button>
        </form>
    </div>
}

export default Post;
